scene.action:
mul(w,%2,190);
add(w,10);
set(plugin[thumpscroll].width, get(w));
if(%1 != NEXT, set(i,1));
if(i LT %2,
	txtadd(imageurl, %3, get(i),'.JPG');
	txtadd(tempurl, %3, get(i),'.JPG');
	txtadd(thumbname,'slide',get(i));
	removeplugin(get(thumbname));
	addplugin(get(thumbname));
	set(plugin[get(thumbname)].url, get(tempurl));
	set(plugin[get(thumbname)].parent, 'layer[thumpscroll]');
	set(plugin[get(thumbname)].align, lefttop);
	set(plugin[get(thumbname)].goc, get(imageurl));
	set(plugin[get(thumbname)].onclick, 'set(layer[slidef].url,get(goc)); if(name == slide1, set(layer[link1].visible, true), set(layer[link1].visible, false));');
	sub(tempi,get(i),1);
	mul(tempx, get(tempi), 190);
	set(plugin[get(thumbname)].x, get(tempx));
	add(plugin[get(thumbname)].x, 10);
	set(plugin[get(thumbname)].y, 15);
	set(plugin[get(thumbname)].width, 180);
	set(plugin[get(thumbname)].height, 90);
	inc(i);
	renderimage(NEXT,%2,%3);
  );
